-- ----------------------------
-- Table structure for goods
-- ----------------------------
DROP TABLE IF EXISTS `goods`;
CREATE TABLE `goods`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '商品id',
  `name` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '商品名称',
  `thumbUrl` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '商品默认图片地址',
  `price` decimal(10, 2) NOT NULL COMMENT '商品默认价格',
  `categoryId` int NOT NULL COMMENT '商品类别id',
  `brandId` int NOT NULL COMMENT '商品品牌id',
  `title` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '商品标题',
  `introduction` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '商品介绍',
  `warningStock` int NOT NULL DEFAULT 100 COMMENT '预警库存',
  `maxBuy` int NOT NULL DEFAULT 5 COMMENT '单次最大购买量',
  `unit` varchar(5) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '个' COMMENT '商品单位',
  `sketchType` int NOT NULL DEFAULT 0 COMMENT '商品概述类型 0.图片 1.网页',
  `sketch` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '商品概述',
  `specs` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '商品参数',
  `skuType` int NOT NULL DEFAULT 0 COMMENT '规格类型 0.单规格 1.多规格',
  `status` int NOT NULL DEFAULT 0 COMMENT '状态 0.上架 1.下架',
  `createTime` datetime(0) NOT NULL DEFAULT CURRENT_TIMESTAMP(0) COMMENT '创建时间',
  `updateTime` datetime(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `name`(`name`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 100 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

