  `score` double(3, 1) NOT NULL DEFAULT 5.0 COMMENT '分数',
  `content` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '评论内容',
  `photoUrls` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '分享的图片集合',
