DROP TABLE IF EXISTS `order`;
CREATE TABLE `order`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '订单id',
  `orderNo` char(18) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '订单号',
  `userId` int NOT NULL COMMENT '用户id',
  `status` int NOT NULL DEFAULT 0 COMMENT '订单状态 0.已下单 2.已付款 4.已配货 6.已发货 8.已收货 10.已评价 30.退款中 32.已退款 34.退款失败 40.已取消',
  `goodsAmount` decimal(10, 2) NOT NULL COMMENT '商品总金额',
  `expressFee` decimal(6, 2) NOT NULL DEFAULT 10.00 COMMENT '快递费',
  `totalAmount` decimal(10, 2) NOT NULL COMMENT '订单总金额',
  `discountAmount` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '优惠金额',
  `payAmount` decimal(10, 2) NOT NULL COMMENT '实付金额',
  `payMode` int NULL DEFAULT NULL COMMENT '支付方式 0.支付宝 1.微信',
  `payPlatform` int NULL DEFAULT NULL COMMENT '支付平台 0.pc 1.app 2.小程序 3.手机H5',
  `ipAddress` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '请求付款时设备ip地址',
  `paymentNo` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '付款平台的支付单号',
  `remark` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '订单备注',
  `createTime` datetime(0) NOT NULL DEFAULT CURRENT_TIMESTAMP(0) COMMENT '创建时间',
  `updateTime` datetime(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `orderNo`(`orderNo`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 89 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

