DROP TABLE IF EXISTS `user`;
CREATE TABLE `user`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '用户id',
  `name` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '掌沃用户' COMMENT '用户昵称',
  `avatarUrl` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'https://cdn.cnbj1.fds.api.mi-img.com/user-avatar/p01tUQzeI3Hl/P9pAUpYwXC1Oq5.jpg' COMMENT '头像地址',
