  `motto` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '个性签名',
  `status` int NOT NULL DEFAULT 0 COMMENT '状态 0.正常 1.冻结',
  `token` char(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '登录后的令牌',
  `expireTime` datetime(0) NULL DEFAULT NULL COMMENT 'token过期时间',
  `loggedTime` datetime(0) NULL DEFAULT NULL COMMENT '最后登录时间',
  `loggedIp` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '最后登录的设备ip地址',
  `createFrom` int NOT NULL DEFAULT 0 COMMENT '注册来源 0.pc平台 1.app平台 2.小程序平台 3.手机H5平台 4.管理平台',
  `createTime` datetime(0) NOT NULL DEFAULT CURRENT_TIMESTAMP(0) COMMENT '创建时间',
  `updateTime` datetime(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 38 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

